{"id":3216,"pattern":"IFRAME","requestType":"AUTHORIZATION","ctaScripts":[{"cssUrl":null,"jsUrl":"https://www.paypal.com/sdk/js?currency=${input.additionalData.currency}&client-id=${vars.restClientId}&intent=authorize&components=buttons","html":"<div id="paypal-button-container"></div>
<script>
  	var scriptIdentifier = '${input.additionalData.scriptIdentifier}';
	var upscaleOrder;
	var upscalePaymentSessionID;
	var pickupStoreLabel = '${vars.pickupStoreLabel}' || 'Free in-store pickup';
	var accountId = '${input.accountId}';
	var orderLines =
		JSON.parse('[<#list input.ctaProductItems as item>{"productId":"${item.productId}","quantity":${item.quantity},"fulfillmentLocationId":"${input.additionalData.fulfillmentLocationId}"}<#if item?has_next>,</#if></#list>]');

	var isAllBOPISOrder = false;
	var styleConfig = {};

	var teardown = function () {
		Upscale.payments.global.stopLoadIndicator();
		upscaleOrder = undefined;
		upscalePaymentSessionID = undefined;
	};

	var onShippingChangeCallbackForBOPIS = async function (data, actions) {
		//console.log('on shipping change callback for BOPIS ....');
		try {
			response = await Upscale.payments.global.initiate(upscaleOrder, accountId);
		} catch (err) {
			console.error('initiate failed', err);
			return actions.reject();
		}
		//console.log('initiate success');
		upscalePaymentSessionID = response.upscalePaymentSessionID;
		// start to patch Paypal order with total updated
		var patchOrderBody = [
			{
				op: 'replace',
				path: "/purchase_units/@reference_id=='default'/amount",
				value: {
					currency_code: upscaleOrder.currency,
					value: upscaleOrder.totalStr,
				},
			},
			{
				op: 'replace',
				path: "/purchase_units/@reference_id=='default'/custom_id",
				value: upscalePaymentSessionID,
			},
		];
		try {
			response = await actions.order.patch(patchOrderBody);
		} catch (err) {
			console.error('Paypal patch order failed', err);
			return actions.reject();
		}
		//console.log('Paypal patch order success', response);
		return actions.resolve();
	};

	var onShippingChangeCallbackForSFDC = async function (data, actions) {
		//console.log('on shipping change callback for SFDC ....');
		//console.log(data, actions);
		var upscaleCalcCostBody = {
			shippingAddress: {
				city: data.shipping_address.city,
				zip: data.shipping_address.postal_code,
				state: data.shipping_address.state,
				country: data.shipping_address.country_code,
			},
		};
		var response = undefined;
		try {
			response = await Upscale.payments.global.calculateCost(upscaleOrder.orderId, upscaleCalcCostBody);
		} catch (err) {
			console.error('calculateCost 1 failed', err);
			return actions.reject();
		}
		//console.log('calculateCost 1 success');
		var PatchOrderForShippingMethodsBody = [];

		const needPatchShippingMethods = response.shippingMethods.findIndex(method => method.id == data.selected_shipping_option?.id) == -1;
		if (needPatchShippingMethods) {
			// need to patch the Paypal order with shipping method options
			var shippingAddressWithMethods = response.shippingMethods.map(function (method, index) {
				return {
					id: method.id,
					label: method.name,
					type: 'SHIPPING',
					amount: {
						currency_code: upscaleOrder.currency,
						value: method.cost.toString(),
					},
					selected: !index,
				};
			});
			PatchOrderForShippingMethodsBody = [
				{
					op: 'add',
					path: "/purchase_units/@reference_id=='default'/shipping/options",
					value: shippingAddressWithMethods,
				},
			];
		}
		var shippingMethodSelected = needPatchShippingMethods
			? response.shippingMethods[0]
			: response.shippingMethods.find(function (method) {
					return method.id == data.selected_shipping_option.id;
			  });
		var shippingMethodReq = {
			shippingAddress: upscaleCalcCostBody.shippingAddress,
			shippingMethod: shippingMethodSelected,
		};
		try {
			response = await Upscale.payments.global.calculateCost(upscaleOrder.orderId, shippingMethodReq);
		} catch (err) {
			console.error('calculateCost 2 failed', err);
			return actions.reject();
		}
		//console.log('calculateCost 2 success');
		upscaleOrder = response.order;
		try {
			response = await Upscale.payments.global.initiate(upscaleOrder, accountId);
		} catch (err) {
			console.error('initiate failed', err);
			return actions.reject();
		}
		//console.log('initiate success');
		upscalePaymentSessionID = response.upscalePaymentSessionID;
		// start to patch Paypal order with total updated
		var patchOrderBody = [
			{
				op: 'replace',
				path: "/purchase_units/@reference_id=='default'/amount",
				value: {
					currency_code: upscaleOrder.currency,
					value: upscaleOrder.totalStr,
				},
			},
			{
				op: 'replace',
				path: "/purchase_units/@reference_id=='default'/custom_id",
				value: upscalePaymentSessionID,
			},
		];
		if (needPatchShippingMethods) {
			patchOrderBody = [...patchOrderBody, ...PatchOrderForShippingMethodsBody];
		}
		try {
			response = await actions.order.patch(patchOrderBody);
		} catch (err) {
			console.error('Paypal patch order failed', err);
			return actions.reject();
		}
		//console.log('Paypal patch order success', response);
		return actions.resolve();
	};

	var onApproveCallbackForBOPIS = async function (data, actions) {
		var patchOrderBody = [
			{
				op: 'replace',
				path: "/purchase_units/@reference_id=='default'/shipping/name",
				value: {
					full_name: `S2S ` + pickupStoreLabel,
				},
			},
		];
		try {
			response = await actions.order.patch(patchOrderBody);
		} catch (err) {
			console.error(err);
			onErrorCallback('Paypal patch BOPIS order with shipping name failed');
		}
	};

	var createOrderFunction = function (data, actions) {
		//console.log('create order ....');
		var orderCreationBody = {
			purchase_units: [
				{
					custom_id: upscaleOrder.orderId,
					amount: {
						currency_code: upscaleOrder.currency,
						value: upscaleOrder.totalStr,
					},
				},
			],
			application_context: {
				shipping_preference: 'GET_FROM_FILE',
			},
		};
		// check the order deliveryMethod type
		if (upscaleOrder.orderLines && upscaleOrder.orderLines.length > 0) {
			isAllBOPISOrder = upscaleOrder.orderLines.every(ol => ol.deliveryMethod == 'BOPIS');
			if (isAllBOPISOrder) {
				var BOPISShippingOptions = {
					options: [
						{
							id: 'BOPIS',
							label: pickupStoreLabel,
							type: 'PICKUP',
							selected: true,
							amount: {
								value: '0.00',
								currency_code: upscaleOrder.currency,
							},
						},
					],
					name: {
						full_name: `S2S ` + pickupStoreLabel,
					},
				};
				orderCreationBody.purchase_units[0].shipping = BOPISShippingOptions;
			}
		} else {
			// upscale order without orderLine errors
			onErrorCallback('upscale order without orderLines.');
		}

		// create paypal order
		return actions.order.create(orderCreationBody);
	};

	var onCancelCallback = function (data) {
		//console.log('on cancel ...', data);
		teardown();
	};

	var onErrorCallback = function (err) {
		console.error(err);
		teardown();
		Upscale.payments.global.throwPaymentError();
	};

	var onClickCallback = async function () {
		//console.log('on click ...');
		try {
            //console.log('Button clicked - creating orderlines with', orderLines);
			const order = await Upscale.payments.global.getOrder(orderLines);
			if (order) {
